import { StatusBar } from 'expo-status-bar';
import React, { useState } from 'react';
import { StyleSheet, View, Platform } from 'react-native';
import { Button, TextInput } from 'react-native-paper';

import ItemList from './components/itemList.component';

export default function App() {
  const [items, setItems] = useState([]);
  const [text, setText] = useState('');

  function onAddItem() {
    const id = items.length + 1;

    const item = {
      id,
      name: text,
      ischecked: false,
    };

    setItems([...items, item]);
    setText('');
  }

  function onDeleteItem(itemId) {
    const updatedItems = items.filter((item) => item.id != itemId);
    setItems(updatedItems);
  }

  function onCheckItem(itemId) {
    const item = items.find((item) => item.id == itemId);
    item.ischecked = true;
    const filteredItems = items.filter((item) => item.id != itemId);
    setItems([...filteredItems, item]);
  }

  return (
    <View style={styles.screen}>
      <View style={styles.inputContainer}>
        <TextInput label="AÃ±adir" style={styles.input} value={text} onChangeText={(input) => setText(input)} />
        <Button mode="contained" onPress={() => onAddItem()}>
          +
        </Button>
      </View>

      <ItemList items={items} onDeleteItem={onDeleteItem} onCheckItem={onCheckItem} />
      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  screen: {
    backgroundColor: '#E1E8EB',
    flex: 1,
    paddingTop: Platform.OS !== 'ios' ? 25 : 50,
    paddingLeft: 30,
    paddingRight: 30,
    paddingBottom: 30,
  },
  inputContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
  },
  input: {
    flex: 0.9,
  },
});
