import React, { useState } from "react";
import { StyleSheet, View } from "react-native";
// import { Button } from "react-native-paper";
import { NavigationContainer } from "@react-navigation/native";
import { createStackNavigator } from "@react-navigation/stack";

import HomeScreen from "./screens/home.screen";
import ListScreen from "./screens/list.screen";
import AddScreen from "./screens/add.screen";

import { useFonts } from "expo-font";
import AppLoading from "expo-app-loading";

function App() {
  const [items, setItems] = useState([]);
  // const [isAddScreen, setIsAddScreen] = useState(true);

  // const [loaded] = useFonts({
  //   "KronaOne-Regular": require("./assets/fonts/KronaOne-Regular.ttf"),
  // });

  // if (!loaded) return <AppLoading />;

  const Stack = createStackNavigator();

  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName="Home">
        <Stack.Screen name="Home" component={HomeScreen} />
        <Stack.Screen name="AddScreen">
          {(props) => (
            <AddScreen items={items} setItems={setItems} {...props} />
          )}
        </Stack.Screen>
        <Stack.Screen name="ListScreen">
          {(props) => <ListScreen items={items} {...props} />}
        </Stack.Screen>
      </Stack.Navigator>
    </NavigationContainer>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
});

export default App;
