import React, { useState } from 'react';
import { StyleSheet, Text, View, Button } from 'react-native';

import ModalItem from './modalItem.component';

export default function Item({ data, onDelete, onCheck }) {
  const [modalVisible, setModalVisible] = useState(false);

  console.log('item2: ', data);
  return (
    <View key={data.id} style={styles.container}>
      <Text>{data.name}</Text>
      {data.ischecked === false && (
        <View style={styles.buttonContainer}>
          <Button
            title="   X   "
            color="#FFC107"
            style={styles.button}
            onPress={() => setModalVisible(true)}
          />
          <Button title="   âœ“   " color="#7952B3" style={styles.button} onPress={() => onCheck(data.id)} />
        </View>
      )}
      <ModalItem modalVisible={modalVisible} setModalVisible={setModalVisible} item={data} onDelete={onDelete} />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    alignItems: 'center',
    width: '100%',
  },
  name: {
    fontSize: 25,
    color: '#343A40',
    marginBottom: 5,
  },
  buttonContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-around',
    width: '100%',
  },
  button: {
    padding: 10,
  },
});
