import { StatusBar } from "expo-status-bar";
import React, { useState } from "react";
import { StyleSheet, Text, TextInput, View, Modal } from "react-native";
import { Button } from "react-native-paper";

import ItemList from "./components/itemList.component";
import ModalItem from "./components/modalItem.component";

export default function App() {
  const [items, setItems] = useState([]);
  const [temp, setTemp] = useState("");
  const [modalVisible, setModalVisible] = useState(false);

  function onInputChange(e) {
    const text = e.target.value;
    setTemp(text);
  }

  function onAddItem() {
    const id = items.length + 1;

    const item = {
      id,
      name: temp,
      ischecked: false,
    };

    setItems([...items, item]);
    setTemp("");
  }

  function onDeleteItem(itemId) {
    const updatedItems = items.filter((item) => item.id != itemId);
    setItems(updatedItems);
  }

  function onCheckItem(itemId) {
    const item = items.find((item) => item.id == itemId);
    item.ischecked = true;
    const filteredItems = items.filter((item) => item.id != itemId);
    setItems([...filteredItems, item]);
  }

  return (
    <View style={styles.screen}>
      <View style={styles.inputContainer}>
        <TextInput
          placeholder="Agregar Item"
          style={styles.input}
          onChange={(event) => onInputChange(event)}
          value={temp}
        />
        {/*<Button title="+" color="#AAAAAA" onPress={() => onAddItem()} />*/}
        <Button icon="camera" mode="contained" onPress={() => onAddItem()}>
          +
        </Button>
      </View>
      <ItemList
        items={items}
        onDeleteItem={onDeleteItem}
        onCheckItem={onCheckItem}
      />
      <StatusBar style="auto" />
      {/*<ModalItem
        modalVisible={modalVisible}
        setModalVisible={setModalVisible}
      />*/}
      <Modal
        animationType="slide"
        visible={modalVisible}
        transparent
        onRequestClose={() => {
          setModalVisible(false);
        }}
      >
        <View style={styles.modalContainer}>
          <View style={[styles.modalContent, styles.shadow]}>
            <Text style={styles.modalMessage}>
              Esta seguro que desea borrar este elemento?
            </Text>
            {/*<Text style={styles.modalTitle}>{itemSelected.value}</Text>*/}
            <View>
              <Button title="confirmar" />
            </View>
          </View>
        </View>
      </Modal>
    </View>
  );
}

const styles = StyleSheet.create({
  screen: {
    padding: 30,
    backgroundColor: "#CBCBCB",
    flex: 1,
  },
  inputContainer: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
  },
  input: {
    borderBottomColor: "black",
    borderBottomWidth: 1,
    width: 200,
  },
});
