import { StatusBar } from "expo-status-bar";
import React, { useState } from "react";
import { StyleSheet, View, Platform } from "react-native";
import { Button, TextInput } from "react-native-paper";

import * as Font from "expo-font";
import { AppLoading } from "expo";

import ListScreen from "./screens/list.screen";
import AddScreen from "./screens/add.screen";

const fetchFonts = async () => {
  await Font.loadAsync({
    "open-sans": require("./assets/fonts/KronaOne-Regular.ttf"),
  });
};

function App() {
  const [items, setItems] = useState([]);
  const [isAddScreen, setIsAddScreen] = useState(true);

  const [loaded, error] = useFonts({
    "open-sans": require("./assets/fonts/KronaOne-Regular.ttf"),
  });

  if (!fontsLoaded) return <AppLoading />;

  return (
    <View style={styles.container}>
      {isAddScreen ? (
        <AddScreen items={items} setItems={setItems} />
      ) : (
        <ListScreen items={items} />
      )}

      <Button mode="contained" onPress={() => setIsAddScreen(!isAddScreen)}>
        {isAddScreen
          ? (style = styles.title("Ver Modo Lista"))
          : (style = styles.title("Agregar Item"))}
      </Button>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  title: {
    fontSize: 20,
    fontFamily: "open-sans",
  },
});

export default App;
